import editorimport cursesimport argparseimport osif __name__ == '__main__':    parser = argparse.ArgumentParser()    parser.add_argument('--file', '-f', help='path to file', required=True)    args = parser.parse_args()    if not os.path.exists(args.file):        file = open(args.file, "wb")        file.close()    bedit = editor.BinEditor(args.file)    curses.wrapper(bedit.main)